        {
            ActionResult _result = null;
            System.Collections.Generic.List<DSS5_SupplyChainFinancialsOptimisation.BO.CreditNoteProduct> creditNoteProducts = new System.Collections.Generic.List<DSS5_SupplyChainFinancialsOptimisation.BO.CreditNoteProduct>();
            foreach (var ord in orders ?? Enumerable.Empty<DSS5_SupplyChainFinancialsOptimisation.BO.Order>())
            {
                foreach (var prod in ord?.OrderProducts ?? Enumerable.Empty<DSS5_SupplyChainFinancialsOptimisation.BO.OrderProduct>())
                {
                    DSS5_SupplyChainFinancialsOptimisation.BO.CreditNoteProduct credProd = new DSS5_SupplyChainFinancialsOptimisation.BO.CreditNoteProduct();
                    credProd.MeasurementUnit = (prod?.MeasurementUnit ?? "");
                    credProd.Price = (prod?.Price ?? 0);
                    credProd.Quantity = (prod?.Quantity ?? 0);
                    credProd.UnitValue = (prod?.UnitValue ?? "");
                    credProd.Code = (prod?.Code ?? "");
                    credProd.Description = (prod?.Description ?? "");
                    creditNoteProducts?.Add(credProd);
                }
            }
            @model.CreditNote.CreditNoteProducts = creditNoteProducts.ToList();
            @model.CreditNote.Orders = orders.ToList();
            return _result;
        }
